// The signUpUser function, defined in the head script, wraps the createUserWithEmailAndPassword function.
// It is exposed globally so that it can be called from anywhere in your application
window.onAuthStateChanged((user) => {
  if (user) {
    // User is signed in, see docs for a list of available properties
    // https://firebase.google.com/docs/reference/js/auth.user

    // The user's ID, unique to the Firebase project. Do NOT use this value 
    // to authenticate with your backend server if you have one. Use User.getIdToken() instead.
    const uid = user.uid;
    const verified = user.emailVerified;
    const anon = user.isAnonymous;
    const userTenantId = user.tenantId;
    const creationTime = user.metadata.creationTime;
    const lastSignInTime = user.metadata.lastSignInTime;

    Outputs.userUid(uid);
    Outputs.emailVerified(verified);
    Outputs.isAnonymous(anon);
    Outputs.tenantId(userTenantId);
    Outputs.userCreationTime(creationTime);
    Outputs.userLastSignInTime(lastSignInTime);

    // Get the JWT
    user.getIdToken().then((token) => {
      Outputs.userJwtToken(token); // Output the JWT
    }).catch((error) => {
      console.error("Error getting token: ", error);
    });

    // Get a user's provider-specific profile info such as display name, email, etc.
    user.providerData.forEach((userInfo, index) => {
      Outputs.userDisplayName(index, userInfo.displayName);
      Outputs.userEmail(index, userInfo.email);
      Outputs.providerId(index, userInfo.providerId);
      Outputs.userPhotoURL(index, userInfo.photoURL);
    });
    // ...
  } else {
    // User is signed out
    // ...
  }
});

// The signUpUser function, defined in the head script, wraps the createUserWithEmailAndPassword function.
// It is exposed globally so that it can be called from anywhere in your application
window.signInUser(Inputs.Email, Inputs.Password)
  .then((userCredential) => {
    // Signed in
    const user = userCredential.user;
    Outputs.Success(); // Signal success for further actions
    // Additional logic for user setup can be added here
  })
  .catch((error) => {
    const errorCode = error.code;
    const errorMessage = error.message;
    Outputs.Failure({ errorCode, errorMessage }); // Signal failure with error details
  });

