    Outputs.Failure({ errorCode, errorMessage }); // Signal failure with error details
  });

if (window.firebaseApp) {
    console.log("Firebase client is already set up");
    // Store the already initialized Firebase app instance to a Noodle var for future reference
    Noodl.Variables.app = window.firebaseApp; 
    console.log("[Initialize] Firebase client is now set up");
    Outputs.Success(); // Indicate success
} else {
    console.error("[Initialize] Firebase client is not initialized");
    Outputs.Failure(); // Indicate failure
}
