function setAuthPersistence() {
    const persistenceOptions = {
        'browserLocalPersistence': window.browserLocalPersistence,
        'browserSessionPersistence': window.browserSessionPersistence,
        'inMemoryPersistence': window.inMemoryPersistence
    };

    // Directly use Inputs.statePersistence to access the correct Firebase persistence value
    const selectedPersistence = persistenceOptions[Inputs.statePersistence];

    if (!selectedPersistence) {
        console.error('Invalid statePersistence value:', Inputs.statePersistence);
        Outputs.Failure();
        return;
    }   

    // Use the globally available setPersistence function
    window.setPersistence(auth, selectedPersistence).then(() => {
        console.log("Auth state persistence set to " + Inputs.statePersistence);
        Outputs.Success();
        Outputs.state = Inputs.statePersistence;
    }).catch((error) => {
        console.error("Failed to set auth state persistence:", error);
        Outputs.Failure();
    });
}

// Call the function to set the auth persistence based on Inputs.statePersistence
setAuthPersistence();

