async function uploadImageToFirebaseStorage() {
  if (!Noodl.Variables.firebaseApp || !Noodl.Variables.firebaseStorage) {
    console.error("Firebase Storage is not set up.");
    Outputs.Failure();
    return;
  }

  const file = Inputs.file;
  const folderName = Inputs.folderName;
  const customName = Inputs.name;

  // Log the file object and its properties for debugging
  console.log("Received file object:", file);
  if (file) {
    console.log(`Is file a File object: ${file instanceof File}`);
    console.log(`File name: ${file.name}, File type: ${file.type}, File size: ${file.size} bytes`);
  }

  if (!file || !folderName || !customName) {
    console.error("File, folder name, or custom name is missing.");
    Outputs.Failure();
    return;
  }

  // Derive the file extension from the contentType
  let extension = file.type.split('/').pop();
  // Construct the full file name with extension
  const fileName = `${customName}.${extension}`;

  // Directly use Noodl.Variables.firebaseStorage
  const storageRef = window.ref(Noodl.Variables.firebaseStorage, `${folderName}/${fileName}`);

  try {
    const uploadResult = await window.uploadBytes(storageRef, file);
    const downloadURL = await window.getDownloadURL(uploadResult.ref);

    // Outputting relevant data upon successful upload
    Outputs.FileURL = downloadURL;
    Outputs.fileName = fileName;
    console.log("Image uploaded successfully: ", downloadURL);
    Outputs.Success();
  } catch (error) {
    console.error("Error uploading image: ", error);
