if (!Boolean(Noodl.Variables.firebaseAuth)) {
  console.error("Firebase is not configured or initialized!");
  return; // Stops the function if Firebase App is not initialized
}

// Initialize the firebase Auth service
const auth = window.getAuth();

auth.onAuthStateChanged((user) => {
  if (user) {
    // User is signed in
    console.log("User is signed in");

    // Store user details in Noodl.Objects
    Noodl.Objects.currentUser = {
      uid: user.uid,
      emailVerified: user.emailVerified,
      isAnonymous: user.isAnonymous,
      tenantId: user.tenantId,
      userCreationTime: user.metadata.creationTime,
      userLastSignInTime: user.metadata.lastSignInTime,
      refreshToken: user.refreshToken,
      providerData: user.providerData.map((userInfo) => ({
        displayName: userInfo.displayName,
        email: userInfo.email,
        phoneNumber: userInfo.phoneNumber,
        providerId: userInfo.providerId,
        photoURL: userInfo.photoURL
      }))
    };

    // Indicate the user is logged in
    Outputs.loggedIn = Boolean(user);
    Outputs.email = user.email;
    Outputs.emailVerified = user.emailVerified;

    // Get the JWT ID Token and output it
    user.getIdToken().then((token) => {
      Outputs.idToken = token;

    }).catch((error) => {
      console.error("Error getting ID token: ", error);
    });
  } else {
    // User is signed out
    console.log("User is signed out");
    Noodl.Objects.currentUser = null; // Clear the current user object
    Outputs.loggedIn = false; // Indicate user is not logged in
  }
  Outputs.Done();
});

if (Boolean(Noodl.Variables.firebaseApp)) {
    console.log("Firebase client is already set up");
    return; // Stops if the client already exists
}

// Inputs from the Noodl component, provided manually via the component's UI
const firebaseConfig = {
    apiKey: Inputs.apiKey,
    authDomain: Inputs.authDomain,
    projectId: Inputs.projectId,
    storageBucket: Inputs.storageBucket,
    messagingSenderId: Inputs.messagingSenderId,
    appId: Inputs.appId,
};

try {
    // Utilize the global 'window' object to import and initialize Firebase, ensuring Firebase SDKs are loaded
    // This assumes Firebase SDKs are available in the global scope
    const app = window.initializeApp(firebaseConfig);
    const auth = window.getAuth(app);

    // Store the Firebase app instance in Noodl variables for global access within the Noodl project
    Noodl.Variables.firebaseAuth = auth;

    console.log("[Initialize] Firebase is now initialized");
    Outputs.Success(); // Signal success in initialization
} catch (e) {
    console.error("[Initialize] Error initializing Firebase: ", e);
    Outputs.Failure(); // Signal failure in initialization
}

if (!Boolean(Noodl.Variables.firebaseAuth)) {
    console.error("Firebase Auth is not configured!");
    Outputs.Failure();
    return; // Stops the function if Firebase Auth is not initialized
}

// Retrieve the email address from the input
const email = Inputs.email;

if (email) {
    // Call sendPasswordResetEmail for the provided email
    window.sendPasswordResetEmail(Noodl.Variables.firebaseAuth, email)
        .then(() => {
            console.log("Password reset email sent.");
            Outputs.email = email;
            Outputs.Success();
        })
        .catch((error) => {
            console.error("Error sending password reset email: ", error.code, error.message);
            console.log("Caught error code:", error.code); // Log the error code
            // Handle specific errors with Outputs.error
            switch (error.code) {
                case "auth/invalid-email":
                    Outputs.error = "Invalid email";
                    break;
//                case "auth/user-not-found":
//                    Outputs.error = "User not found"; //
//                    break;
                default:
                    Outputs.error = "Password reset email send failed";
            }
            Outputs.Failure();
        });
} else {
    console.log("Email address is required.");
    Outputs.error = "Email address is required";
    Outputs.Failure();
}

if (Boolean(Noodl.Variables.firebaseAuth)) {
  Noodl.Variables.firebaseAuth.signOut().then(() => {
    console.log("User signed out successfully.");
    Outputs.Success();
    // Update the UI or perform other actions post-sign-out
    // For example, resetting user-related variables or navigating to the login screen
  }).catch((error) => {
    console.error("Sign out error:", error);
    // Handle any errors that occur during sign out
    Outputs.Failure();
  });
} else {
  console.error("Firebase Auth is not configured or initialized!");
}

const email = Inputs.email;

if (window.validator && window.validator.isEmail(email)) {
    console.log("The email address is valid.");
    Outputs.isValid = true;
} else {
    console.log("The email address is invalid.");
    Outputs.isValid = false;
    Outputs.Failure();
}

// Signal the completion of the operation
Outputs.Done();

if (!Boolean(Noodl.Variables.firebaseAuth)) {
    console.error("Firebase Auth is not configured!");
    Outputs.error = "Firebase Auth is not configured";
    Outputs.Failure();
    return; // Stops the function if Firebase Auth is not initialized
}

// Retrieve the email and password from Noodl inputs
const email = Inputs.email;
const password = Inputs.password;

console.log("Attempting to sign in with email: ", email);

if (email && password) {
    // Use the globally available signInWithEmailAndPassword function for signing in
    window.signInWithEmailAndPassword(Noodl.Variables.firebaseAuth, email, password)
        .then((userCredential) => {
            const user = userCredential.user;
            console.log(`User signed in successfully, emailVerified: ${user.emailVerified}`);

            // Update the currentUser object in Noodl.Objects
            Noodl.Objects.currentUser = {
                uid: user.uid,
                email: user.email,
                emailVerified: user.emailVerified,
                refreshToken: user.refreshToken,
                // providerData and other fields will be populated by onAuthStateChanged (get currenUser)
            };
            Outputs.loggedIn = true; // User is logged in regardless of email verif status to enable the sendEmailVerification function
            Outputs.Success();
            if (!user.emailVerified) {
                Outputs.error = "Email not verified. Please check the email verification link sent to you during sign-up, or request a new link below.";
                Outputs.isNotVerified = true; // Signal for triggering sendEmailVerification button
                console.log("User email not verified");
            } else {
                Outputs.isNotVerified = false;
                console.log("User email verified");
            }
            Outputs.Success();
        })
        .catch((error) => {
            console.error("Error signing in: ", error.code, error.message);
            console.log(`Error details - code: ${error.code}, message: ${error.message}`);
            Outputs.showAlert = true;

// It is recommended to enable Email Enumeration Protection:
// https://cloud.google.com/identity-platform/docs/admin/email-enumeration-protection#overview
// This protection lets the API be intentionally vague about the inputs issue, making it harder for attackers to figure out any user's account credential.

            // Handle errors with Outputs.error
            let errorMessage;
            switch (error.code) {
                case "auth/user-not-found": // This error msg will only fire if Email Enumeration Protection is disabled
                    errorMessage = "Sign-in failed"; // No "User not found" as explicit message to prevent attackers from determining whether an email is associated with an account
                    break;
                case "auth/wrong-password": // This error msg will only fire if Email Enumeration Protection is disabled
                    errorMessage = "Wrong password";
                    break;
                case "auth/invalid-email": // This error msg will only fire if Email Enumeration Protection is disabled
                    errorMessage = "Invalid email address format, it may be missing the @ symbol, the domain, or having invalid characters in the email portion";
                    break;
                case "auth/invalid-credential":
                    errorMessage = "Invalid credential, please verify your inputs";
                    break;
                case "auth/too-many-requests":
                    errorMessage = "Sign-in blocked, too many requests. You can immediately restore it by resetting your password or you can try again later.";
                    break;
                default:
                    errorMessage = "Sign-in failed";
            }
            console.log("Firebase error:", errorMessage, error.code); // Debug: Ensure this logs correctly
            Outputs.error = errorMessage;
            Outputs.loggedIn = false;
            Outputs.showAlert = true;
            Outputs.Failure();
        });
} else {
    console.log("Email and password are required.");
    Outputs.error = "Email and password are required";
    Outputs.showAlert = true;
    Outputs.Failure();
}

if (!Boolean(Noodl.Variables.firebaseAuth)) {
    console.error("Firebase Auth is not configured!");
    Outputs.error = "Firebase Auth is not configured";
    Outputs.Failure();
    return; // Stops the function if Firebase Auth is not initialized
}

window.createUserWithEmailAndPassword(Noodl.Variables.firebaseAuth, Inputs.email, Inputs.password)
    .then((userCredential) => {
        console.log("User signed up");
        Outputs.Success();
    })
    .catch((error) => {
        console.error("Error signing up: ", error.code, error.message);
        console.log("Caught error code:", error.code); // Log the error code

        switch (error.code) {
            case "auth/email-already-in-use":
                Outputs.error = "Email already in use - please use another one";
                break;
            case "auth/invalid-email":
                Outputs.error = "Invalid email address format, it may be missing the @ symbol, the domain, or having invalid characters in the email portion";
                break;
            case "auth/weak-password":
                Outputs.error = "Password too weak";
                break;
            case "auth/missing-password":
                Outputs.error = "Password missing";
                break;
            default:
                Outputs.error = "An error occurred during sign up.";
        }
        Outputs.Failure();
    });

if (!Boolean(Noodl.Variables.firebaseAuth)) {
    console.error("Firebase Auth is not configured!");
    return; // Stops the function if Firebase Auth is not initialized
}

// Assuming Inputs.email contains the new email address to update to
const newEmail = Inputs.email;

if (newEmail) {
    // Use the globally available updateEmail function
    window.verifyBeforeUpdateEmail(Noodl.Variables.firebaseAuth.currentUser, newEmail)
        .then(() => {
            console.log("Email updated successfully.");
            Noodl.Objects.currentUser.email = newEmail; // Update the email in Noodl.Objects.currentUser
            Outputs.newEmail = newEmail; // Reflect the successful email update
            Outputs.showAlert = false;
            Outputs.Success();
        })
        .catch((error) => {
            console.error("Error updating email: ", error.code, error.message);
            // Handle specific errors with Outputs.error
            switch (error.code) {
                case "auth/email-already-in-use":
                    Outputs.error = "Email already in use";
                    break;
                case "auth/invalid-email":
                    Outputs.error = "Email update failed.";
                    break;
                case "auth/requires-recent-login":
                    Outputs.error = "Requires recent login. Please re-authenticate.";
                    Outputs.requiresLogin = true;
                    break;
                case "auth/operation-not-allowed":
                    Outputs.error = "Not allowed.";
                    break;
                default:
                    Outputs.error = "Email update failed.";
            }
            Outputs.showAlert = true;
            Outputs.Failure();
        });
} else {
    console.log("New email address is required.");
    Outputs.error = "New email address is required";
    Outputs.showAlert = true;
    Outputs.Failure();
}

if (!Boolean(Noodl.Variables.firebaseAuth)) {
    console.error("Firebase Auth is not configured!");
    Outputs.Failure();
    return; // Stops the function if Firebase Auth is not initialized
}

const user = Noodl.Variables.firebaseAuth.currentUser;
const email = Inputs.email;
const password = Inputs.password;

// Re-authenticate user by using the reauthenticateWithCredential method on the user object
if (user) {
    const credential = window.EmailAuthProvider.credential(email, password);
    window.reauthenticateWithCredential(user, credential).then(() => {
        console.log("User re-authenticated.");
        Outputs.Success();
    }).catch((error) => {
        console.error("Error re-authenticating user: ", error);
        switch (error.code) {
            case "auth/user-mismatch":
                Outputs.error = "The provided credentials do not match the logged-in user.";
                break;
            case "auth/user-not-found":
                Outputs.error = "No user corresponding to the provided credentials could be found.";
                break;
            case "auth/invalid-credential":
                Outputs.error = "The provided credential is invalid.";
                break;
            case "auth/invalid-email":
                Outputs.error = "The email address is not valid.";
                break;
            case "auth/wrong-password":
                Outputs.error = "Wrong password.";
                break;
            default:
                Outputs.error = "An error occurred during re-authentication.";
        }
        Outputs.Failure();
    });
} else {
    console.log("No user is currently signed in.");
}
